import {createSlice} from '@reduxjs/toolkit';

const authSlice=createSlice({
    name:'auth-slice',
    initialState:{
        auth:false
    },
    reducers:{
        login(state,action){
            state.auth=action.payload.true;
        }
    }
});

const {actions,reducer} =authSlice;

export const {login}=actions;
export default reducer;
